// 动态加载PDF
document.addEventListener('DOMContentLoaded', () => {
  const iframe = document.getElementById('pdf-iframe');

  // PDF路径
  const pdfUrl = '/books/vol22.pdf';

  // 解决所有浏览器兼容问题
  iframe.src = `${pdfUrl}#view=FitH&toolbar=0&navpanes=0&scrollbar=0`;

  // 全屏控制
  document.querySelector('.pdf-fullscreen-btn').addEventListener('click', () => {
    iframe.requestFullscreen?.() || 
    iframe.webkitRequestFullscreen?.();
  });
});



/* 核心样式 */
.ultimate-pdf-container {
  position: relative;
  width: 100%;
  height: 80vh; /* 默认高度 */
  margin: 30px 0;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 8px 20px rgba(0,0,0,0.12);
}

#pdf-iframe {
  width: 100%;
  height: 100%;
  border: none;
  background: #f5f5f7;
}

.pdf-fullscreen-btn {
  position: absolute;
  bottom: 25px;
  right: 25px;
  padding: 12px 24px;
  font-size: 18px;
  background: linear-gradient(135deg, #3a7bd5, #00d2ff);
  color: white;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  z-index: 10;
  opacity: 0.95;
  transition: all 0.3s;
  font-weight: bold;
}

.pdf-fullscreen-btn:hover {
  opacity: 1;
  transform: scale(1.05);
}

/* 移动端专属优化 */
@media (max-width: 768px) {
  .ultimate-pdf-container {
    height: 100vh; /* 手机全屏高度 */
    border-radius: 0;
    margin: 0;
  }

  .pdf-fullscreen-btn {
    bottom: 15px;
    right: 15px;
    padding: 10px 20px;
    font-size: 16px;
  }

  /* 强制隐藏手机浏览器工具栏 */
  #pdf-iframe {
    height: 100vh !important;
  }
}




